mapper.select:
SELECT * FROM (SELECT result1.*, ROWNUM as rnum FROM (SELECT num, writer, title, imagePath, regdate, viewCount, rating, reservetype FROM board_review
ORDER BY num DESC) result1)
WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}

mapper.update:
UPDATE board_review
SET viewCount=viewCount+1
WHERE num=#{num}
